        roomStructuresExpiration[this.name] = getCacheExpiration(STRUCTURE_TIMEOUT);
        roomStructureIDs[this.name] = _.mapValues(_.groupBy(this.find(FIND_STRUCTURES), (s) => s.structureType), (structures) => _.map(structures, s => s.id));
